contracts/Raffle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title Raffle
 * @notice On-chain raffle system for Intuition Network with secure reward deposits and payouts
 * @dev Allows users to enter raffles with shard payment and picks winners using on-chain randomness
 * @dev Implements secure escrow system for reward tokens (TRUST)
 */
contract Raffle is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    struct RaidRaffle {
        address creator;
        string title;
        uint256 entryFee; // Amount of shards required to enter
        uint256 numberOfWinners;
        uint256 startTime;
        uint256 endTime;
        bool isActive;
        bool winnersPicked;
        address[] entries;
        address[] winners;
        mapping(address => bool) hasEntered;

        // Reward system fields
        address rewardToken;           // TRUST token contract address
        uint256 totalRewardAmount;    // Total deposited rewards
        uint256[] winnerRewards;      // Reward amount per winner (in order)
        bool rewardsDeposited;        // Whether rewards are locked in escrow
        bool rewardsDistributed;      // Whether rewards have been paid out
    }

    // Mapping from raid ID to raffle data
    mapping(string => RaidRaffle) public raffles;

    // Mapping to track user's shard balance (in a real system, this would be an ERC20 token)
    mapping(address => uint256) public shardBalances;

    // Security: Track if a raffle ID exists
    mapping(string => bool) public raffleExists;

    // Security: Minimum reward amount to prevent dust attacks
    uint256 public constant MIN_REWARD_AMOUNT = 1e15; // 0.001 tokens (assuming 18 decimals)

    // Security: Maximum number of winners to prevent gas issues
    uint256 public constant MAX_WINNERS = 100;

    // Events
    event RaffleCreated(
        string indexed raidId,
        address indexed creator,
        string title,
        uint256 entryFee,
        uint256 numberOfWinners,
        uint256 endTime,
        address rewardToken,
        uint256 totalRewardAmount
    );

    event RewardsDeposited(
        string indexed raidId,
        address indexed creator,
        address rewardToken,
        uint256 totalAmount
    );

    event EntrySubmitted(
        string indexed raidId,
        address indexed entrant,
        uint256 entryFee
    );

    event WinnersPicked(
        string indexed raidId,
        address[] winners,
        uint256[] rewards
    );

    event RewardsDistributed(
        string indexed raidId,
        address[] winners,
        uint256[] amounts
    );

    event RewardsRefunded(
        string indexed raidId,
        address indexed creator,
        address rewardToken,
        uint256 amount
    );

    event ShardsDeposited(address indexed user, uint256 amount);
    event ShardsWithdrawn(address indexed user, uint256 amount);

    // Modifiers
    modifier onlyCreator(string memory raidId) {
        require(
            raffles[raidId].creator == msg.sender,
            "Only creator can perform this action"
        );
        _;
    }

    modifier raffleExistsCheck(string memory raidId) {
        require(
            raffleExists[raidId],
            "Raffle does not exist"
        );
        _;
    }

    modifier validRewardToken(address token) {
        require(token != address(0), "Invalid reward token address");
        require(token.code.length > 0, "Token must be a contract");
        _;
    }

    constructor() Ownable(msg.sender) {}

    /**
     * @notice Create a new raffle with reward deposits
     * @param raidId Unique identifier for the raid
     * @param title Title of the raffle
     * @param entryFee Number of shards required to enter
     * @param numberOfWinners Number of winners to pick
     * @param endTime Timestamp when raffle ends
     * @param rewardToken Address of the reward token (TRUST)
     * @param totalRewardAmount Total amount of rewards to deposit
     * @param winnerRewards Array of reward amounts per winner (must sum to totalRewardAmount)
     * @dev Security: Validates all inputs, prevents duplicate raffles, locks rewards in escrow
     */
    function createRaffle(
        string memory raidId,
        string memory title,
        uint256 entryFee,
        uint256 numberOfWinners,
        uint256 endTime,
        address rewardToken,
        uint256 totalRewardAmount,
        uint256[] memory winnerRewards
    ) external nonReentrant validRewardToken(rewardToken) {
        // Security: Prevent duplicate raffles
        require(!raffleExists[raidId], "Raffle already exists");

        // Security: Validate inputs
        require(bytes(raidId).length > 0, "Raid ID cannot be empty");
        require(bytes(title).length > 0, "Title cannot be empty");
        require(entryFee > 0, "Entry fee must be greater than 0");
        require(numberOfWinners > 0, "Must have at least 1 winner");
        require(numberOfWinners <= MAX_WINNERS, "Too many winners");
        require(endTime > block.timestamp, "End time must be in the future");
        require(totalRewardAmount >= MIN_REWARD_AMOUNT, "Reward amount too small");
        require(winnerRewards.length == numberOfWinners, "Winner rewards length mismatch");

        // Security: Validate winner rewards sum equals total
        uint256 sum = 0;
        for (uint256 i = 0; i < winnerRewards.length; i++) {
            require(winnerRewards[i] > 0, "Each winner must have a reward");
            sum += winnerRewards[i];
        }
        require(sum == totalRewardAmount, "Winner rewards must sum to total");

        // Security: Transfer reward tokens from creator to contract (escrow)
        IERC20 token = IERC20(rewardToken);
        uint256 balanceBefore = token.balanceOf(address(this));
        token.safeTransferFrom(msg.sender, address(this), totalRewardAmount);
        uint256 balanceAfter = token.balanceOf(address(this));

        // Security: Verify transfer succeeded (protect against fee-on-transfer tokens)
        require(balanceAfter - balanceBefore == totalRewardAmount, "Token transfer failed");

        // Initialize raffle
        RaidRaffle storage raffle = raffles[raidId];
        raffle.creator = msg.sender;
        raffle.title = title;
        raffle.entryFee = entryFee;
        raffle.numberOfWinners = numberOfWinners;
        raffle.startTime = block.timestamp;
        raffle.endTime = endTime;
        raffle.isActive = true;
        raffle.rewardToken = rewardToken;
        raffle.totalRewardAmount = totalRewardAmount;
        raffle.winnerRewards = winnerRewards;
        raffle.rewardsDeposited = true;
        raffle.rewardsDistributed = false;

        raffleExists[raidId] = true;

        emit RaffleCreated(
            raidId,
            msg.sender,
            title,
            entryFee,
            numberOfWinners,
            endTime,
            rewardToken,
            totalRewardAmount
        );

        emit RewardsDeposited(raidId, msg.sender, rewardToken, totalRewardAmount);
    }

    /**
     * @notice Enter a raffle by paying the entry fee in shards
     * @param raidId Unique identifier for the raid
     * @dev Security: Prevents re-entry, validates raffle state, uses checks-effects-interactions
     */
    function enterRaffle(string memory raidId) 
        external 
        nonReentrant
        raffleExistsCheck(raidId) 
    {
        RaidRaffle storage raffle = raffles[raidId];

        // Security: Validate raffle state
        require(raffle.isActive, "Raffle is not active");
        require(block.timestamp < raffle.endTime, "Raffle has ended");
        require(!raffle.hasEntered[msg.sender], "Already entered this raffle");
        require(
            shardBalances[msg.sender] >= raffle.entryFee,
            "Insufficient shard balance"
        );
        require(raffle.rewardsDeposited, "Rewards not deposited");

        // Checks-Effects-Interactions pattern for security
        // Effect: Deduct shards first
        shardBalances[msg.sender] -= raffle.entryFee;

        // Effect: Add entry
        raffle.entries.push(msg.sender);
        raffle.hasEntered[msg.sender] = true;

        emit EntrySubmitted(raidId, msg.sender, raffle.entryFee);
    }

    /**
     * @notice Pick winners using on-chain randomness and distribute rewards
     * @param raidId Unique identifier for the raid
     * @dev Security: Uses secure randomness, prevents double-picking, auto-distributes rewards
     * @dev Can be called by anyone after the raffle ends (decentralized)
     */
    function pickWinners(string memory raidId) 
        external 
        nonReentrant
        raffleExistsCheck(raidId)
    {
        RaidRaffle storage raffle = raffles[raidId];

        // Security: Validate state
        require(block.timestamp >= raffle.endTime, "Raffle has not ended yet");
        require(!raffle.winnersPicked, "Winners already picked");
        require(raffle.entries.length > 0, "No entries to pick from");
        require(raffle.rewardsDeposited, "Rewards not deposited");
        require(!raffle.rewardsDistributed, "Rewards already distributed");

        uint256 numWinners = raffle.numberOfWinners;
        if (numWinners > raffle.entries.length) {
            numWinners = raffle.entries.length;
        }

        // Security: Create a copy of entries to avoid modifying the original array during selection
        address[] memory availableEntries = new address[](raffle.entries.length);
        for (uint256 i = 0; i < raffle.entries.length; i++) {
            availableEntries[i] = raffle.entries[i];
        }

        // Pick winners using secure on-chain randomness
        address[] memory selectedWinners = new address[](numWinners);
        for (uint256 i = 0; i < numWinners; i++) {
            uint256 randomIndex = _getRandomNumber(
                availableEntries.length - i,
                block.timestamp,
                block.prevrandao,
                i
            );

            // Swap selected entry with the last available entry
            address selected = availableEntries[randomIndex];
            availableEntries[randomIndex] = availableEntries[availableEntries.length - 1 - i];

            selectedWinners[i] = selected;
            raffle.winners.push(selected);
        }

        // Effect: Update state before interactions
        raffle.winnersPicked = true;
        raffle.isActive = false;

        emit WinnersPicked(raidId, selectedWinners, raffle.winnerRewards);

        // Interaction: Distribute rewards to winners
        _distributeRewards(raidId, selectedWinners);
    }

    /**
     * @notice Internal function to distribute rewards to winners
     * @param raidId Unique identifier for the raid
     * @param winners Array of winner addresses
     * @dev Security: Uses SafeERC20, prevents reentrancy, validates amounts
     */
    function _distributeRewards(string memory raidId, address[] memory winners) internal {
        RaidRaffle storage raffle = raffles[raidId];

        require(!raffle.rewardsDistributed, "Rewards already distributed");
        require(raffle.rewardToken != address(0), "Invalid reward token");

        IERC20 token = IERC20(raffle.rewardToken);
        uint256[] memory distributedAmounts = new uint256[](winners.length);

        // Security: Distribute rewards using SafeERC20
        for (uint256 i = 0; i < winners.length; i++) {
            require(winners[i] != address(0), "Invalid winner address");

            uint256 rewardAmount = 0;
            if (i < raffle.winnerRewards.length) {
                rewardAmount = raffle.winnerRewards[i];
            }

            if (rewardAmount > 0) {
                // Security: Use SafeERC20 to handle tokens that don't return true
                token.safeTransfer(winners[i], rewardAmount);
                distributedAmounts[i] = rewardAmount;
            }
        }

        // Effect: Mark rewards as distributed
        raffle.rewardsDistributed = true;

        emit RewardsDistributed(raidId, winners, distributedAmounts);
    }

    /**
     * @notice Refund rewards to creator (only if raffle hasn't ended and no winners picked)
     * @param raidId Unique identifier for the raid
     * @dev Security: Only creator can refund, only before winners are picked
     */
    function refundRewards(string memory raidId) 
        external 
        nonReentrant
        onlyCreator(raidId)
        raffleExistsCheck(raidId)
    {
        RaidRaffle storage raffle = raffles[raidId];

        require(raffle.rewardsDeposited, "No rewards to refund");
        require(!raffle.rewardsDistributed, "Rewards already distributed");
        require(!raffle.winnersPicked, "Cannot refund after winners picked");
        require(block.timestamp < raffle.endTime, "Cannot refund after raffle ends");

        address tokenAddress = raffle.rewardToken;
        uint256 amount = raffle.totalRewardAmount;

        // Effect: Clear rewards state
        raffle.rewardsDeposited = false;
        raffle.totalRewardAmount = 0;
        delete raffle.winnerRewards;

        // Interaction: Transfer tokens back to creator
        IERC20(tokenAddress).safeTransfer(raffle.creator, amount);

        emit RewardsRefunded(raidId, raffle.creator, tokenAddress, amount);
    }

    /**
     * @notice Get a random number using block properties
     * @param max Maximum value (exclusive)
     * @param timestamp Current block timestamp
     * @param prevrandao Block prevrandao (EIP-4399)
     * @param nonce Additional nonce for uniqueness
     * @return Random number between 0 and max-1
     * @dev Security: Uses multiple sources of randomness for better distribution
     */
    function _getRandomNumber(
        uint256 max,
        uint256 timestamp,
        uint256 prevrandao,
        uint256 nonce
    ) internal view returns (uint256) {
        require(max > 0, "Max must be greater than 0");

        // Security: Combine multiple sources of randomness for better distribution
        uint256 seed = uint256(
            keccak256(
                abi.encodePacked(
                    blockhash(block.number - 1),
                    timestamp,
                    prevrandao,
                    nonce,
                    msg.sender,
                    tx.origin,
                    block.difficulty
                )
            )
        );
        return seed % max;
    }

    /**
     * @notice Get raffle information
     * @param raidId Unique identifier for the raid
     */
    function getRaffleInfo(string memory raidId)
        external
        view
        returns (
            address creator,
            string memory title,
            uint256 entryFee,
            uint256 numberOfWinners,
            uint256 startTime,
            uint256 endTime,
            bool isActive,
            bool winnersPicked,
            uint256 entryCount,
            address[] memory winners,
            address rewardToken,
            uint256 totalRewardAmount,
            uint256[] memory winnerRewards,
            bool rewardsDeposited,
            bool rewardsDistributed
        )
    {
        RaidRaffle storage raffle = raffles[raidId];
        return (
            raffle.creator,
            raffle.title,
            raffle.entryFee,
            raffle.numberOfWinners,
            raffle.startTime,
            raffle.endTime,
            raffle.isActive,
            raffle.winnersPicked,
            raffle.entries.length,
            raffle.winners,
            raffle.rewardToken,
            raffle.totalRewardAmount,
            raffle.winnerRewards,
            raffle.rewardsDeposited,
            raffle.rewardsDistributed
        );
    }

    /**
     * @notice Get all entries for a raffle
     * @param raidId Unique identifier for the raffle
     */
    function getEntries(string memory raidId)
        external
        view
        returns (address[] memory)
    {
        return raffles[raidId].entries;
    }

    /**
     * @notice Check if an address has entered a raffle
     * @param raidId Unique identifier for the raffle
     * @param entrant Address to check
     */
    function hasEntered(string memory raidId, address entrant)
        external
        view
        returns (bool)
    {
        return raffles[raidId].hasEntered[entrant];
    }

    /**
     * @notice Deposit shards (for testing - in production, this would be an ERC20 transfer)
     * @param amount Amount of shards to deposit
     * @dev Security: Input validation
     */
    function depositShards(uint256 amount) external {
        require(amount > 0, "Amount must be greater than 0");
        shardBalances[msg.sender] += amount;
        emit ShardsDeposited(msg.sender, amount);
    }

    /**
     * @notice Get shard balance for an address
     * @param user Address to check
     */
    function getShardBalance(address user) external view returns (uint256) {
        return shardBalances[user];
    }

    /**
     * @notice Emergency function to recover stuck tokens (only owner)
     * @param token Address of the token to recover
     * @param to Address to send tokens to
     * @param amount Amount to recover
     * @dev Security: Only owner can call, should only be used in emergencies
     */
    function emergencyRecover(address token, address to, uint256 amount) 
        external 
        onlyOwner 
    {
        require(to != address(0), "Invalid recipient");
        IERC20(token).safeTransfer(to, amount);
    }
}

