// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title ClaimQuest
 * @notice Contract that creates a claim (triple) when users claim/complete quests on Intuition Protocol
 * @dev Creates a triple using MultiVault with subject (user), predicate (completed), and object (quest)
 * 
 * The triple termId returned matches what the SDK returns when creating triples:
 * const triple = await createTripleStatement(...)
 * console.log('Triple ID:', triple.state.termId) // bytes32 termId
 */
interface IMultiVault {
    /**
     * @notice Creates multiple triple vaults with initial deposits
     * @param subjectIds Array of atom IDs to use as subjects
     * @param predicateIds Array of atom IDs to use as predicates
     * @param objectIds Array of atom IDs to use as objects
     * @param assets Array of asset amounts to deposit into each triple vault
     * @return Array of triple IDs (termIds) as bytes32 for the created triples
     */
    function createTriples(
        bytes32[] calldata subjectIds,
        bytes32[] calldata predicateIds,
        bytes32[] calldata objectIds,
        uint256[] calldata assets
    ) external payable returns (bytes32[] memory);

    /**
     * @notice Get the cost required to create a triple
     * @return The cost in native token (TRUST)
     */
    function getTripleCost() external view returns (uint256);
}

contract ClaimQuest {
    IMultiVault public immutable multiVault;
    address public immutable revenueWallet;
    uint256 public constant DAPP_FEE_PERCENTAGE = 30;
    uint256 public constant FEE_DENOMINATOR = 100;

    event QuestClaimed(
        address indexed claimer,
        bytes32 indexed claimTermId,
        bytes32 indexed questTermId,
        bytes32 userTermId,
        bytes32 predicateTermId
    );

    error InvalidAddress();
    error InvalidTermId();
    error InsufficientValue(uint256 required, uint256 provided);
    error FeeTransferFailed();
    error TripleCreationFailed();

    constructor(address _multiVault, address _revenueWallet) {
        if (_multiVault == address(0) || _revenueWallet == address(0)) {
            revert InvalidAddress();
        }
        multiVault = IMultiVault(_multiVault);
        revenueWallet = _revenueWallet;
    }

    /**
     * @notice Claim a quest by creating a triple claim on Intuition Protocol
     * @param userTermId The user's identity atom termId (subject)
     * @param predicateTermId The predicate atom termId (e.g., "completed") 
     * @param questTermId The quest atom termId (object)
     * @return claimTermId The created claim triple termId (bytes32)
     * 
     * Creates a triple: (userTermId, predicateTermId, questTermId)
     * This represents: "User completed Quest"
     * 
     * The termId returned matches the SDK pattern:
     * - Frontend: triple.state.termId (from createTripleStatement)
     * - GraphQL: Use getTripleDetails(termId) to fetch triple details
     */
    function claimQuest(
        bytes32 userTermId,
        bytes32 predicateTermId,
        bytes32 questTermId
    ) external payable returns (bytes32 claimTermId) {
        // Validate termIds are not zero
        if (userTermId == bytes32(0) || predicateTermId == bytes32(0) || questTermId == bytes32(0)) {
            revert InvalidTermId();
        }

        uint256 tripleCost = multiVault.getTripleCost();

        if (msg.value < tripleCost) {
            revert InsufficientValue(tripleCost, msg.value);
        }

        uint256 remainingBalance = msg.value - tripleCost;
        uint256 dAppFee = (remainingBalance * DAPP_FEE_PERCENTAGE) / (FEE_DENOMINATOR + DAPP_FEE_PERCENTAGE);

        // Prepare arrays for batch creation (single triple in this case)
        bytes32[] memory subjectIds = new bytes32[](1);
        bytes32[] memory predicateIds = new bytes32[](1);
        bytes32[] memory objectIds = new bytes32[](1);
        uint256[] memory assets = new uint256[](1);
        
        subjectIds[0] = userTermId;
        predicateIds[0] = predicateTermId;
        objectIds[0] = questTermId;
        assets[0] = 0; // No initial deposit, just creation fee

        // Create triple and get termId
        bytes32[] memory termIds = multiVault.createTriples{value: tripleCost}(
            subjectIds,
            predicateIds,
            objectIds,
            assets
        );
        
        if (termIds.length == 0 || termIds[0] == bytes32(0)) {
            revert TripleCreationFailed();
        }

        claimTermId = termIds[0];

        // Transfer dApp fee if any
        if (dAppFee > 0) {
            (bool success, ) = revenueWallet.call{value: dAppFee}("");
            if (!success) {
                revert FeeTransferFailed();
            }
        }

        emit QuestClaimed(
            msg.sender,
            claimTermId,
            questTermId,
            userTermId,
            predicateTermId
        );

        return claimTermId;
    }

    /**
     * @notice Get the cost required to claim a quest (create a triple)
     */
    function getClaimCost() external view returns (uint256) {
        return multiVault.getTripleCost();
    }

    receive() external payable {}
}

