// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title CreateQuest
 * @notice Contract that creates a quest by registering a claim (atom) on Intuition Protocol
 * @dev Creates an atom using MultiVault and returns the termId (bytes32)
 * 
 * The termId returned matches what the SDK returns when creating atoms:
 * const result = await createAtomFromString(...)
 * console.log('Quest Atom ID:', result.state.termId) // bytes32 termId
 */
interface IMultiVault {
    /**
     * @notice Creates multiple atom vaults with initial deposits
     * @param atomDatas Array of atom data (metadata) for each atom to be created
     * @param assets Array of asset amounts to deposit into each atom vault
     * @return Array of atom IDs (termIds) as bytes32 for the created atoms
     */
    function createAtoms(
        bytes[] calldata atomDatas,
        uint256[] calldata assets
    ) external payable returns (bytes32[] memory);

    /**
     * @notice Get the cost required to create an atom
     * @return The cost in native token (TRUST)
     */
    function getAtomCost() external view returns (uint256);
}

contract CreateQuest {
    IMultiVault public immutable multiVault;
    address public immutable revenueWallet;
    uint256 public constant DAPP_FEE_PERCENTAGE = 30;
    uint256 public constant FEE_DENOMINATOR = 100;

    event QuestCreated(
        address indexed creator,
        bytes32 indexed questTermId,
        string questName
    );

    error InvalidAddress();
    error InsufficientValue(uint256 required, uint256 provided);
    error FeeTransferFailed();
    error AtomCreationFailed();

    constructor(address _multiVault, address _revenueWallet) {
        if (_multiVault == address(0) || _revenueWallet == address(0)) {
            revert InvalidAddress();
        }
        multiVault = IMultiVault(_multiVault);
        revenueWallet = _revenueWallet;
    }

    /**
     * @notice Create a quest by registering a claim (atom) on Intuition Protocol
     * @param questName The name of the quest (used as the atom data on Intuition)
     * @return questTermId The created quest atom termId (bytes32)
     * 
     * The termId returned matches the SDK pattern:
     * - Frontend: result.state.termId (from createAtomFromString)
     * - GraphQL: Use getAtomDetails(termId) to fetch atom details
     */
    function createQuest(string calldata questName) external payable returns (bytes32 questTermId) {
        uint256 atomCost = multiVault.getAtomCost();

        if (msg.value < atomCost) {
            revert InsufficientValue(atomCost, msg.value);
        }

        uint256 remainingBalance = msg.value - atomCost;
        uint256 dAppFee = (remainingBalance * DAPP_FEE_PERCENTAGE) / (FEE_DENOMINATOR + DAPP_FEE_PERCENTAGE);

        bytes memory questNameBytes = bytes(questName);
        if (questNameBytes.length == 0) {
            revert AtomCreationFailed();
        }

        // Prepare arrays for batch creation (single atom in this case)
        bytes[] memory atomDatas = new bytes[](1);
        uint256[] memory assets = new uint256[](1);
        atomDatas[0] = questNameBytes;
        assets[0] = 0; // No initial deposit, just creation fee

        // Create atom and get termId
        bytes32[] memory termIds = multiVault.createAtoms{value: atomCost}(atomDatas, assets);

        if (termIds.length == 0 || termIds[0] == bytes32(0)) {
            revert AtomCreationFailed();
        }

        questTermId = termIds[0];

        // Transfer dApp fee if any
        if (dAppFee > 0) {
            (bool success, ) = revenueWallet.call{value: dAppFee}("");
            if (!success) {
                revert FeeTransferFailed();
            }
        }

        emit QuestCreated(msg.sender, questTermId, questName);

        return questTermId;
    }

    /**
     * @notice Get the cost required to create a quest
     */
    function getQuestCost() external view returns (uint256) {
        return multiVault.getAtomCost();
    }

    receive() external payable {}
}

