contracts/CreateSpace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title CreateSpace
 * @notice Minimal gas-efficient contract for creating space identities (atoms) on Intuition Protocol
 * @dev Directly calls MultiVault.createAtoms with minimal overhead for single transaction space creation
 */
interface IMultiVault {
    function createAtoms(
        bytes[] calldata atomDatas,
        uint256[] calldata assets
    ) external payable returns (bytes32[] memory);
    
    function getAtomCost() external view returns (uint256);
}

contract CreateSpace {
    IMultiVault public immutable multiVault;

    event SpaceCreated(
        address indexed creator,
        bytes32 indexed spaceTermId,
        string spaceName
    );

    error InvalidAddress();
    error InsufficientValue(uint256 required, uint256 provided);
    error CreationFailed();

    constructor(address _multiVault) {
        if (_multiVault == address(0)) revert InvalidAddress();
        multiVault = IMultiVault(_multiVault);
    }

    /**
     * @notice Create a space identity by registering an atom on Intuition Protocol
     * @param spaceName The name of the space (used as the atom data)
     * @return spaceTermId The created space atom termId (bytes32)
     * 
     * Minimal gas usage: Only validates input, calls MultiVault, and emits event
     */
    function createSpace(string calldata spaceName) external payable returns (bytes32 spaceTermId) {
        uint256 atomCost = multiVault.getAtomCost();
        
        if (msg.value < atomCost) {
            revert InsufficientValue(atomCost, msg.value);
        }

        bytes memory spaceNameBytes = bytes(spaceName);
        if (spaceNameBytes.length == 0) {
            revert CreationFailed();
        }

        // Prepare single atom creation
        bytes[] memory atomDatas = new bytes[](1);
        uint256[] memory assets = new uint256[](1);
        atomDatas[0] = spaceNameBytes;
        assets[0] = 0; // No initial deposit

        // Create atom - forward exact cost to MultiVault
        bytes32[] memory termIds = multiVault.createAtoms{value: atomCost}(atomDatas, assets);
        
        if (termIds.length == 0 || termIds[0] == bytes32(0)) {
            revert CreationFailed();
        }

        spaceTermId = termIds[0];

        // Refund any excess value
        uint256 excess = msg.value - atomCost;
        if (excess > 0) {
            (bool success, ) = msg.sender.call{value: excess}("");
            // Don't revert on refund failure - atom was created successfully
            if (!success) {
                // If refund fails, allow excess to remain in contract
                // Users can withdraw later if needed
            }
        }

        emit SpaceCreated(msg.sender, spaceTermId, spaceName);

        return spaceTermId;
    }

    /**
     * @notice Get the cost required to create a space identity
     */
    function getSpaceCost() external view returns (uint256) {
        return multiVault.getAtomCost();
    }

    /**
     * @notice Allow users to withdraw any excess funds if refund failed
     */
    function withdrawExcess() external {
        uint256 balance = address(this).balance;
        if (balance > 0) {
            (bool success, ) = msg.sender.call{value: balance}("");
            require(success, "Withdrawal failed");
        }
    }

    receive() external payable {}
}

